import React, { useState } from "react";
import axios from "axios";

export default function Contact() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [status, setStatus] = useState("");

  const handleChange = (e) => setForm({...form, [e.target.name]: e.target.value});

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("sending");
    try {
      // change URL if your backend runs elsewhere
      await axios.post("http://localhost:5000/api/contact", form);
      setStatus("sent");
      setForm({name:"",email:"",message:""});
    } catch (err) {
      console.error(err);
      setStatus("error");
    }
  };

  return (
    <main className="page">
      <h1 className="page-title">Contact Us!</h1>

      <form className="contact-form" onSubmit={handleSubmit}>
        <input name="name" placeholder="Your name" value={form.name} onChange={handleChange} required />
        <input name="email" type="email" placeholder="Your email" value={form.email} onChange={handleChange} required />
        <textarea name="message" placeholder="Message" rows="6" value={form.message} onChange={handleChange} required />
        <div>
          <button className="btn btn-primary" type="submit">Send</button>
          <span style={{marginLeft:12}}>
            {status === "sending" && "Sending..."}
            {status === "sent" && "Message sent — check your email!"}
            {status === "error" && "Error sending. Try again."}
          </span>
        </div>
      </form>
    </main>
  );
}
